document.addEventListener("DOMContentLoaded",(()=>{const e=document.getElementById("menu-toggle"),t=document.getElementById("nav-links");e&&t&&(e.addEventListener("click",(()=>{e.classList.toggle("active"),t.classList.toggle("active"),document.body.classList.toggle("menu-open")})),e.addEventListener("touchend",(()=>{e.classList.toggle("active"),t.classList.toggle("active"),document.body.classList.toggle("menu-open")})),t.querySelectorAll("a").forEach((o=>{o.addEventListener("click",(()=>{e.classList.remove("active"),t.classList.remove("active"),document.body.classList.remove("menu-open")}))}))),document.querySelectorAll('a[href^="#"]').forEach((e=>{e.addEventListener("click",(t=>{t.preventDefault();const o=e.getAttribute("href");if("#"===o)return;const n=document.querySelector(o);if(n){const e=document.querySelector(".main-nav").offsetHeight,t=n.getBoundingClientRect().top+window.pageYOffset-e;window.scrollTo({top:t,behavior:"smooth"})}}))}));const o=document.querySelector(".main-nav");window.addEventListener("scroll",(()=>{window.scrollY>100?o.classList.add("scrolled"):o.classList.remove("scrolled")}))}));